<script>
import { store } from "../store.js";
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
import { faHome, faUtensils, faUser, faCog } from '@fortawesome/free-solid-svg-icons';

export default {
    components: {
        FontAwesomeIcon
    },
    name: "SideBarComponent",
    data() {
        return {
            store,
        }
    },

    methods: {
        getImagePath(imageName) {
            return new URL(`../assets/${imageName}`, import.meta.url).href
        }
    },
    computed: {
        backendUrl() {
            return this.store.urlBackend;
        },
        cartQuantity() {
            let output = 0;
            this.store.cart.forEach(element => {
                output += element.quantity
            })
            return output; // Calcoliamo la lunghezza dell'array del carrello
        }
    }
}
</script>

